import axios from "axios";

export const getComments =(postId)=> async (dispatch) => {
    console.log(postId)
    const res = await axios.get("http://localhost:8000/getcomments", {
        headers:{postId: postId}
    });
    dispatch({
        type: "GET_COMMENTS",
        payload: res,
    })
};

export const comment = (token, commentCont) => async (dispatch)=>{
    const res = await axios.post("http://localhost:8000/createcomment", commentCont,{
        headers:{ Authorization: "Bearer "+ token },
    });
    dispatch({
        type: "CREATE_COMMENT",
        payload:res,
    })

};
