import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Avatar } from "@material-ui/core"
import { post } from "../../_actions/getPosts.js";
import { Form, Button, TextArea } from "semantic-ui-react";
import { getComments, comment } from "./../../_actions/getComments";
import './Tweetbox.css'



function Commentbox(props) {
console.log(props)
    const dispatch = useDispatch();
    const id = props.id;
    const postId=props.postId
    const token = props.token;
    const [userPost, updateUser] = userPost({ author: id, postId: postId, comment: "" });


    return (
        <div className="tweetBox">


            <div className="tweetBox__input">
                <Avatar src="https://images.unsplash.com/photo-1511367461989-f85a21fda167?ixid=MnwxMjA3fDB8MHxzZWFyY2h8Mnx8cHJvZmlsZXxlbnwwfHwwfHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=500&q=60" />

                <Form
                    onSubmit={(e) => {

                        dispatch(comment(token, userPost)).then(() => {
                            dispatch(getComments(post._id))
                            updateUser({ ...userPost, postId: "", comment: "" });
                            // history.push(`/${props.tabName}`)
                        });
                    }}
                >
                    <TextArea style={{ height: 50, width: 375 }}
                        type="text"
                        value={userPost.comment}
                        placeholder="Create a new Post!"
                        onChange={(e) => {
                            updateUser({ ...userPost, postId: post._id, comment: e.target.value });
                            console.log(e.target.value, post._id)
                        }}
                    ></TextArea>
                    <Button>Create Post</Button>
                </Form>

            </div>

        </div>
    )
}

export default Commentbox
