                                            //             if (comment._id === post._id)
                                            //                 updateUser({ ...userPost, postId: post._id, comment: e.target.value });
                                            //             console.log(e.target.value, comment)
                                            //         }}

                                            //     />
                                            //     <Button content='Add Reply' labelPosition='left' icon='edit' primary />
                                            // </Form>
                                        if (comment.postId === post._id) {

                                            return (

                                                // <Comment.Group>
                                                //     <Comment>
                                                //         <Comment.Avatar as='a' src='https://react.semantic-ui.com/images/avatar/small/joe.jpg' />
                                                //         <Comment.Content>
                                                //             <Comment.Author>Tom Lukic</Comment.Author>
                                                //             <Comment.Text>
                                                //                 This will be great for business reports. I will definitely download
                                                //                 this.
                                                //              </Comment.Text>
                                                //             <Comment.Actions>
                                                //                 <Comment.Action>Reply</Comment.Action>
                                                //                 <Comment.Action>Save</Comment.Action>
                                                //                 <Comment.Action>Hide</Comment.Action>
                                                //                 <Comment.Action>
                                                //                     {/* <Icon name='expand' /> */}
                                                //                  Full-screen
                                                //                  </Comment.Action>
                                                //             </Comment.Actions>
                                                //         </Comment.Content>
                                                //     </Comment>
                                                // </Comment.Group>


                                                <Comment>
                                                    <Comment.Content>
                                                        <Comment.Author as='a'>
                                                            <Link style={{ textDecoration: 'none', color: '#50b7f5' }}

                                                                to={{
                                                                    pathname: `/profile/${comment.author.name}`,
                                                                    state: {
                                                                        name: comment.author.name,
                                                                        id: comment.author._id,
                                                                        token: token,
                                                                    },
                                                                }}
                                                            >
                                                                {comment.author.name}
                                                            </Link>
                                                            <span className="post_headerSpecial">
                                                                <VerifiedUserIcon className="post_badge"/> @{comment.author.email}
                                                            </span>
                                                            {/* {comment.author.name} */}    
                                                        </Comment.Author>
                                                        <Comment.Text>{comment.comment}</Comment.Text>
                                                    </Comment.Content>
                                                    <br></br>



                                                </Comment>
                                            );
                                            // console.log(comment)
                                        }
                                    }))
                                    }


                                            {/* <div>
                                                <Form reply
                                                    onSubmit={(e) => {

                                                        dispatch(comment(token, userPost)).then(() => {
                                                            dispatch(getComments(post._id))
                                                            updateUser({ ...userPost, postId: "", comment: "" });
                                                            history.push(`/${props.tabName}`)
                                                        });




                                                    }}
                                                >
                                                    <Form.TextArea
                                                        type="text"
                                                        value={userPost.comment}
                                                        placeholder="Create a new Post!"
                                                        onChange={(e) => {
                                                            if (Data.handleComment.postComments===post._id){
                                                                console.log("Alert");
                                                            }
