  var [userData, setUser] = useState(localStorage.getItem("userData"));
  useEffect(() => {
    setUser(localStorage.getItem("userData"));
  }, []);

  const history = useHistory();
  // const dispatch = useDispatch();




  // return (

    if(userData){
       history.push("/home") 
      // alert(JSON.parse(userData).data.result)
      return(
    <div className="App">


          <Switch>
            <Route exact path="/home"
              component={() => <Home userData={JSON.parse(userData).data.result} token={JSON.parse(userData).data.token} />}
            />
          </Switch>

          <Switch>
            <Route path="/profile" 
              component={() => <Profile userData={JSON.parse(userData).data.result} token={JSON.parse(userData).data.token}  />}
            />
          </Switch>



      <div>
            <Button
              primary
              basic
              onClick={() => {
                localStorage.setItem("userData", "");
                userData = "";
                window.location.reload();
                history.push("/");

              }}
            >
              Logout
          </Button>
      </div>


      {/* <Switch>
        <Route exact path="/" component={Landing} />
      </Switch>

