      </Switch>

      <Switch>
        <Route path="/signup" component={Signup} />
      </Switch>

      <Switch>
        <Route path="/profile" component={Profile} />
      </Switch> */}
    </div>
      );
    } else{
      history.push("/")

      return(
        <div className="App">
      <Switch>
        <Route exact path="/" component={Landing} />
      </Switch>


      <Switch>
        <Route path="/login" component={Login} />
      </Switch>

      <Switch>
        <Route path="/signup" component={Signup} />
      </Switch>

      {/* <Switch>
        <Route path="/profile" component={Profile} />
      </Switch> */}
        </div>

      );
    }
  // );
}

export default App;
